package gcometimport org.grails.plugin.gcomet.GCometChannelSubscriberclass ChannelService {	static scope = 'session'	def subscribers = []		def subscribeCurrentUserTo(channel){		def subscriber = new GCometChannelSubscriber()		subscriber.start()		channel.subscribe(subscriber)		subscribers << subscriber	}		def retrieveStateUpdatesForUser(){		subscribers.collectMany{[it.getStates(3000)]}	}}